package net;

public class Alphabet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char test = 'a';

		for(; test<='z'; test++){
			System.out.println(test);		
			}
		test = 'A';
		
		System.out.println();
		
		for(; test<='Z'; test++){
			System.out.println(test);		
			}


	}

}
